from odd.artifact import Addon, AddonPath
from odd.plugin import Plugin
from odd.utils import list_files


class AddonPathEmitter(Plugin):
    _handles = {"addon"}
    _emits = {"addon_path"}

    def on_addon(self, addon: Addon):
        for path in list_files(addon.path, list_dirs=True):
            yield AddonPath(addon, path)
